se Exception(f"Could not find import to remove: {old_import_model}")\\n\\n            self.common_attributes.dependencies.remove(import_model_to_remove)\\n            self.common_attributes.dependencies.append(new_import_model)\\n        else:\\n            raise Exception(\\n                f"No imports in the builders imports list: {self.common_attributes.dependencies}"\\n            )\\n        return self\\n\\n    def build_and_set_children(self) -> None:\\n        if self.children_builders:\\n            self.common_attributes.children = [\\n                child.build() for child in self.children_builders\\n            ]\\n\\n    def _get_common_attributes(self) -> dict[str, Any]:\\n        """\\n        Returns a dictionary containing the attributes common to all code block models.\\n        """\\n        return self.common_attributes.model_dump()\\n\\n    @abstractmethod\\n    def build(\\n        self,\\n    ) -> None:\\n        """\\n        Builds and returns the code block model instance.\\n\\n        Returns:\\n            CodeBlockModel: The built code block model instance.\\n        """\\n        ...\\n\\n```\\n\\nCHILDREN_SUMMARIES:\\n\\nSummary:\\n\\n        [{\\\'content\\\': "You are a code summarizer. Your task is to analyze the code provided and create a concise summary of the\\\\ngiven code based on the prompt provided. Your summary should be technical yet understandable, providing a clear picture of the code\\\'s purpose, main\\\\nfeatures, and key components.\\\\n", \\\'role\\\': \\\'system\\\'}, {\\\'content\\\': \\\'None\\\\n\\\\nCODE:\\\\n\\\\ndef __init__(\\\\n    self, *, id: str, block_type: BlockType, parent_id: str | None\\\\n) -> None:\\\\n    self.id: str = id\\\\n    self.children_builders: list[\\\\n        ClassModelBuilder | FunctionModelBuilder | StandaloneBlockModelBuilder\\\\n    ] = []\\\\n\\\\n    self.common_attributes = BaseCodeBlockModel(\\\\n        id=id,\\\\n        parent_id=parent_id,\\\\n        block_type=block_ty