                        #         child_child_id
                        #     ):
                        #         if child_child.summary:
                        #             child_summary = child_child.summary
                        #             break
                        continue

                    child_summary = (
                        f"Child ({child_id}) code content:\n{child.code_content}\n"
                    )
                child_summary_list.append(child_summary)
    return child_summary_list
