def set_children_ids(
    self,
) -> Union[
    "BaseModelBuilder",
    "ModuleModelBuilder",
    "ClassModelBuilder",
    "FunctionModelBuilder",
]:
    """Sets the children ids of the model instance."""
    self.common_attributes.children_ids = [
        child.id for child in self.child_builders
    ]
    return self
